// WeatherCard.js
import React, { Component } from 'react';
import axios from 'axios';
import './WeatherCard.css';
import sunnyIcon from './su';
import rainyIcon from './icons/rainy.png';
import cloudyIcon from './icons/cloudy.png';
// Add more imports for different weather conditions


export default class WeatherCard extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null,
      forecastData: null,
    };
  }

  componentDidMount() {
    this.fetchWeatherData();
    this.fetchForecastData();
  }

  fetchWeatherData = async () => {
    const { location } = this.props;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=09d75966cd9607042f423dc9c0e6f26d`
      );
      this.setState({ weatherData: response.data });
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  fetchForecastData = async () => {
    const { location } = this.props;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${location}&appid=09d75966cd9607042f423dc9c0e6f26d`
      );
      this.setState({ forecastData: response.data.list });
    } catch (error) {
      console.error('Error fetching forecast data:', error);
    }
  };

  kelvinToCelsius = (kelvin) => {
    return kelvin - 273.15; 
  };

  render() {
    const { location, onRemove } = this.props;
    const { weatherData, forecastData } = this.state;
  
    let weatherIcon;
    if (weatherData) {
      // Determine the weather condition and assign the corresponding icon
      switch (weatherData.weather[0].main.toLowerCase()) {
        case 'clear':
          weatherIcon = sunnyIcon;
          break;
        case 'rain':
          weatherIcon = rainyIcon;
          break;
        case 'clouds':
          weatherIcon = cloudyIcon;
          break;
        // Add more cases for different weather conditions
        default:
          // Default icon if no match is found
          weatherIcon = defaultIcon;
      }
    }
  
    return (
      <div className="weather-card">
        <h2>{location}</h2>
        {weatherIcon && <img src={weatherIcon} alt="Weather Icon" className="weather-icon" />}
        {weatherData && (
          <div>
            <p>Temperature: {this.kelvinToCelsius(weatherData.main.temp).toFixed(2)} Â°C</p>
            <p>Humidity: {weatherData.main.humidity}%</p>
            <p>Wind Speed: {weatherData.wind.speed} m/s</p>
            <p>Visibility: {weatherData.visibility} meters</p>
            <p>Pressure: {weatherData.main.pressure} hPa</p>
          </div>
        )}
        {forecastData && (
          <div>
            <h3>Full Day Forecast</h3>
            {forecastData.slice(0, 8).map((forecast, index) => (
              <div key={index}>
                <p>
                  Date: {new Date(forecast.dt * 1000).toLocaleDateString()}{' '}
                  Time: {new Date(forecast.dt * 1000).toLocaleTimeString()}
                </p>
                <p>Temperature: {this.kelvinToCelsius(forecast.main.temp).toFixed(2)} Â°C</p>
              </div>
            ))}
          </div>
        )}
        {weatherData && <button onClick={onRemove}>Remove Location</button>}
      </div>
    );
  }
}
