// Import necessary dependencies
import React, { Component } from 'react';
import axios from 'axios';
import './WeatherCard.css';

class WeatherCard extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null,
      forecastData: null,
    };
  }

  componentDidMount() {
    this.fetchWeatherData();
    this.fetchForecastData();
  }

  fetchWeatherData = async () => {
    const { location } = this.props;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=09d75966cd9607042f423dc9c0e6f26d`
      );
      this.setState({ weatherData: response.data });
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  fetchForecastData = async () => {
    const { location } = this.props;

    try {
      const response = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${location}&appid=09d75966cd9607042f423dc9c0e6f26d`
      );
      this.setState({ forecastData: response.data.list });
    } catch (error) {
      console.error('Error fetching forecast data:', error);
    }
  };

  kelvinToCelsius = (kelvin) => {
    return kelvin - 273.15;
  };

  getWeatherIcon = (conditionCode) => {
    // Map OpenWeatherMap condition codes to corresponding image filenames
    const imageMap = {
      '01d': './weather-app/sunny.jpeg',
      '01n': '/weather-app/sunny.jpeg',
      '02d': '/weather-app/sunny.jpeg',
      '02n': '/weather-app/sunny.jpeg',
      '03d': '/weather-app/sunny.jpeg',
      '03n': '/weather-app/sunny.jpeg',
      '04d': '/weather-app/sunny.jpeg',
      '04n': '/weather-app/sunny.jpeg',
      '09d': '/weather-app/sunny.jpeg',
      '09n': '/weather-app/sunny.jpeg',
      '10d': '/weather-app/sunny.jpeg',
      '10n': 'rain-night.png',
      '11d': 'thunderstorm-day.png',
      '11n': 'thunderstorm-night.png',
      '13d': 'snow-day.png',
      '13n': 'snow-night.png',
      '50d': 'mist-day.png',
      '50n': 'mist-night.png',
    };

    const imageSrc = imageMap[conditionCode] || 'default-image.png'; // Default image if not found

    return <img src={`images/${imageSrc}`} alt="Weather Icon" />;
  };

  render() {
    const { location, onRemove } = this.props;
    const { weatherData, forecastData } = this.state;

    return (
      <div className="weather-card">
        <h2>{location}</h2>
        {weatherData && (
          <div>
            {this.getWeatherIcon(weatherData.weather[0].icon)}
            <p>Temperature: {this.kelvinToCelsius(weatherData.main.temp).toFixed(2)} °C</p>
            <p>Humidity: {weatherData.main.humidity}%</p>
            <p>Wind Speed: {weatherData.wind.speed} m/s</p>
            <p>Visibility: {weatherData.visibility} meters</p>
            <p>Pressure: {weatherData.main.pressure} hPa</p>
          </div>
        )}
        {forecastData && (
          <div>
            <h3>Full Day Forecast</h3>
            {forecastData.slice(0, 8).map((forecast, index) => (
              <div key={index}>
                <p>
                  Date: {new Date(forecast.dt * 1000).toLocaleDateString()}{' '}
                  Time: {new Date(forecast.dt * 1000).toLocaleTimeString()}
                </p>
                <p>Temperature: {this.kelvinToCelsius(forecast.main.temp).toFixed(2)} °C</p>
              </div>
            ))}
          </div>
        )}
        {weatherData && <button onClick={onRemove}>Remove Location</button>}
      </div>
    );
  }
}

export default WeatherCard;
